INSERT INTO `wp_postmeta` VALUES (288,35,'_mission_banner_picture','field_615c60ea9b2df');
INSERT INTO `wp_postmeta` VALUES (289,35,'mission_description','At AA we embrace diversity, change and inclusiveness. We aim to empower the talent we represent so that their voices can be heard. We believe we are not just a company, but a community. A community that is a safe haven for our talent, where we supply them with the support to be their true selves – where all are encouraged to be bold and brave.\r\n\r\nAA belief in the importance of empowering our talents and promoting diversity is driving the industry towards inclusion. Currently, AA operates offices in New York, Los Angeles, Miami, and Toronto, alongside a trusted network of independent agencies around the world. As AA moves towards a new chapter in our iconic legacy, we invite you to help define what is to come with us.');
INSERT INTO `wp_postmeta` VALUES (290,35,'_mission_description','field_615c60f89b2e0');
